import DisplayUang from "../components/displayUang";
import { useState } from "react";
import Coin from '../assets/character/coin.png'
import DisplayDate from "../components/displayDate";
import LocationQuests from "../data/quests";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Buttons from "../components/buttons";
import FoodButton from "../components/foodButtons";
import Status from "../components/status";



const Indonesia = () => {
    const namaPlayer = localStorage.getItem("namaPlayer")
    const Navigate = useNavigate()

    const [money, setMoney] = useState(100)
    const [bath, setBath] = useState(() => Number(localStorage.getItem("bath")) || 50)
    const [hunger, setHunger] = useState(() => Number(localStorage.getItem("hunger")) || 50)
    const [sleep, setSleep] = useState(() => Number(localStorage.getItem("sleep")) || 50)
    const [happiness, setHappiness] = useState(() => Number(localStorage.getItem("happiness")) || 50)
    const [health, setHealth] = useState(() => Number(localStorage.getItem("health")) || 50)
    const [currentQuest, setCurrentQuest] = useState([])
    const [questStarted, setQuestStarted] = useState(false)
    const [showFoods, setShowFoods] = useState(false)

    useEffect(() => {
    localStorage.setItem("bath", bath);
}, [bath]);
    useEffect(() => {
    localStorage.setItem("hunger", hunger);
}, [hunger]);
useEffect(() => {
    localStorage.setItem("sleep", sleep);
}, [sleep]);
useEffect(() => {
    localStorage.setItem("happiness", happiness);
}, [happiness]);
useEffect(() => {
    localStorage.setItem("health", health);
}, [health]);

    {/* KEUANGAN */}
    function incrementUang(uangJml){
        setMoney(prevMoney => prevMoney + uangJml)
    }

    function decrementUang(uangJml){
        setMoney(prevMoney => prevMoney - uangJml)
    }

    {/* QUEST */ }
    useEffect(() => {
        const savedQuests = JSON.parse(localStorage.getItem("currentQuest")) || [];
        setCurrentQuest(savedQuests);
    }, []);

    const startQuests = () => {
        if (currentQuest.length === 0) {
            // Pilih satu quest acak dari setiap negara
            const questsPerCountry = Object.entries(LocationQuests).map(([negara, quests]) => {
                const randomQuest = quests[Math.floor(Math.random() * quests.length)];
                return randomQuest;
            });

            setCurrentQuest(questsPerCountry);
            localStorage.setItem("currentQuest", JSON.stringify(questsPerCountry));
            alert("Quest baru telah dimulai!");
        } else {
            alert("Selesaikan quest yang ada terlebih dahulu!");
        }
    };

    {/* MANDI */ }

    function incrementShower() {
        let shower = 100
        setBath(bath + shower)
    }

    function decrementShower() {
        setBath(prevBath => prevBath -= 1)
        console.log(bath)
    }

    {/* FOTO */ }
    function handleFoto() {
        alert("Kamu sedang foto")
        setMoney(prevMoney => prevMoney += 25)
    }

    {/* MAKAN */ }
    const foods = [
        { name: "Popmie", cost: "$2", harga: 2, addBar: 20 },
        { name: "Nasi Padang", cost: "$4", harga: 4, addBar: 40 },
        { name: "Eskrim", cost: "$1", harga: 1, addBar: 10 }
    ]

    function MakanClicked(){
        setShowFoods(true)
    }

    function handleMakan(food) {
        const blackScreen = document.getElementById("blackScreen");
        blackScreen.style.opacity = "1";
        blackScreen.style.pointerEvents = "auto";
        
            alert(`Kamu sedang makan ${food.name}`);
            // function cek quest

            // Update bar hunger
            setHunger(prev => Math.min(prev + food.addBar, 100));

            // Kurangi uang
            setMoney(prevMoney => prevMoney - food.harga);

            // Layar hitam menghilang
            blackScreen.style.opacity = "0";
            blackScreen.style.pointerEvents = "none";
            setShowFoods(false)
    }

    {/* SLEEP */ }
    function decrementSleep(){
        setSleep(prevSleep => prevSleep -= 1)
    }
    function incrementSleep(){
        setSleep(prevSleep => prevSleep + 100)
        console.log(sleep)
    }

    {/* TENDA */ }
    function handleTenda(){
        alert("Kamu sedang mengunjungi Paman yang sedang berkemah");
        incrementUang(20)
    }

    return (
        <>
            <audio id="background-music" src="/assets/character/musik/musicIndo.mp3" loop></audio>
            <div className="black-screen" id="blackScreen"></div>
            <div className="frame">
                <div className="camera">
                    <div className="map pixel-art">
                        <div>
                            <div>
                                <div id="quest-display">
                                    <h3>Daftar Semua Quest</h3>
                                    {currentQuest.length === 0 ? (
                                        <p>Tidak ada quest</p>
                                    ) : (
                                        currentQuest.map((quest, index) => (
                                            <div key={index}>
                                                <h4>{quest.name}</h4>
                                                <p>{quest.description}</p>
                                                <span className="info-quest" data-info={`Kamu akan mendapatkan $${quest.gaji}`}>
                                                    {" "} (i)
                                                </span>
                                            </div>
                                        ))
                                    )}
                                </div>
                                <div id="game-day" className="date">
                                    <DisplayDate namaPlayer={namaPlayer} />
                                </div>
                                <div>
                                    <img src={Coin} className="money-picture" style={{ width: '50px' }} />
                                </div>
                                <div className="money-displayed">
                                    <DisplayUang money={money} />
                                </div>
                                <Status bath={bath} hunger={hunger} sleep={sleep} happiness={happiness} health={health}/>
                            </div>
                            <Buttons value="Map" onClick={() => Navigate('/map')} />
                            <Buttons value="Sleep" onClick={incrementSleep}/>
                            <Buttons value="Foto" onClick={handleFoto} />
                            <Buttons value="Mandi" onClick={incrementShower} />
                            <Buttons value="Makan" onClick={MakanClicked} />
                            {showFoods && (
                                <div id="makanOptions">
                                    <h4>Pilih Makanan</h4>
                                    {foods.map((food, index)=>(
                                        <FoodButton key={index} food={food} onClick={handleMakan}/>
                                    ))}
                                </div>
                            )}
                            
                            <Buttons value="Tenda" onClick={handleTenda}/>
                            <Buttons value="Start Quest" onClick={startQuests} />
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Indonesia
