// ...existing code...
import React, { useEffect, useRef, useState } from "react";
import Controller from '../components/Controller';
import DisplayDate from "../components/DisplayDate";
import Quest from "../components/Quest";
import './Indo.css';

const directions = {
    up: "up",
    down: "down",
    left: "left",
    right: "right",
};

function Indo() {
    const selectedCharacter = localStorage.getItem("selectedCharacter");
    const [position, setPosition] = useState({ x: 565, y: 125 });
    const [facing, setFacing] = useState("down");
    const [walking, setWalking] = useState(false);
    const [heldDirections, setHeldDirections] = useState([]);
    const [showGuild, setShowGuild] = useState(false);
    const speed = 1;

    // Refs for animation
    const positionRef = useRef(position);
    const heldDirectionsRef = useRef(heldDirections);

    useEffect(() => {
        heldDirectionsRef.current = heldDirections;
    }, [heldDirections]);

    useEffect(() => {
        positionRef.current = position;
    }, [position]);

    // Guild button logic
    useEffect(() => {
        const guildX = 65;
        const guildY = 100;
        const range = 15;
        if (Math.abs(position.x - guildX) < range && Math.abs(position.y - guildY) < range) {
            setShowGuild(true);
        } else {
            setShowGuild(false);
        }
    }, [position]);

    // Enter key for Guild
    useEffect(() => {
        function handleKeyDown(event) {
            if (event.key === "Enter" && showGuild) {
                handleGuildClick();
            }
        }
        window.addEventListener("keydown", handleKeyDown);
        return () => window.removeEventListener("keydown", handleKeyDown);
        // eslint-disable-next-line
    }, [showGuild]);

    // Animation loop
    useEffect(() => {
        let animationFrameId;
        const step = () => {
            let { x, y } = positionRef.current;
            const held_direction = heldDirectionsRef.current[0];
            let moved = false;
            if (held_direction) {
                if (held_direction === directions.right) x += speed;
                if (held_direction === directions.left) x -= speed;
                if (held_direction === directions.down) y += speed;
                if (held_direction === directions.up) y -= speed;
                setFacing(held_direction);
                setWalking(true);
                moved = true;
            } else {
                setWalking(false);
            }
            // WALL LIMIT
            const leftLimit = 15;
            const rightLimit = 590;
            const topLimit = 100;
            const bottomLimit = 180;
            if (x < leftLimit) x = leftLimit;
            if (x > rightLimit) x = rightLimit;
            if (y < topLimit) y = topLimit;
            if (y > bottomLimit) y = bottomLimit;
            if (moved) setPosition({ x, y });
            positionRef.current = { x, y };
            animationFrameId = window.requestAnimationFrame(step);
        };
        animationFrameId = window.requestAnimationFrame(step);
        return () => window.cancelAnimationFrame(animationFrameId);
    }, []);

    // Style for map and character
    const pixelSize = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--pixel-size')) || 2;
    const camera_left = pixelSize * 66;
    const camera_top = pixelSize * 42;
    const mapStyle = {
        transform: `translate3d(${-position.x * pixelSize + camera_left}px, ${-position.y * pixelSize + camera_top}px, 0)`
    };
    const characterStyle = {
        transform: `translate3d(${position.x * pixelSize}px, ${position.y * pixelSize}px, 0)`
    };

    // Handler for Guild button
    function handleGuildClick() {
        const currentQuest = JSON.parse(localStorage.getItem("currentQuest")) || [];
        if (currentQuest.length === 0) {
            // Panggil fungsi startQuests dari Quest, atau trigger event sesuai kebutuhan
            // Misal: window.dispatchEvent(new Event("startQuests"));
            alert("Quest baru telah dimulai! (Integrasikan dengan Quest)");
        } else {
            alert("Selesaikan semua quest terlebih dahulu!");
        }
    }

    return (
        <>
            <div className="indo-root">
                <div className="black-screen" id="blackScreen"></div>
                <div className="frame">
                    <div className="camera">
                        <div className="indo-map pixel-art" style={mapStyle}>
                            <div>
                                <div
                                    className="character"
                                    facing={facing}
                                    walking={walking ? "true" : "false"}
                                    style={characterStyle}
                                >
                                    <div className="shadow pixel-art"></div>
                                    <div
                                        className="character_spritesheet pixel-art"
                                        style={{
                                            backgroundImage: selectedCharacter
                                                ? `url('${selectedCharacter}')`
                                                : "none"
                                        }}
                                    ></div>
                                </div>
                                <div> <DisplayDate /> </div>
                            </div>
                        </div>
                    </div>
                </div>
                {/* GUILD BUTTON */}
                {showGuild && (
                    <div className="indo0button-guild" style={{ position: "absolute", left: 20, bottom: 80 }}>
                        <button className="guild-button" onClick={handleGuildClick}>
                            Guild
                        </button>
                    </div>
                )}
                <Quest />
                <Controller onDirectionChange={setHeldDirections} />
            </div>
        </>
    );
}

export default Indo;
