import React, { useEffect, useRef, useState } from "react";
import './Indo.css';

function Indo() {
    const [position, setPosition] = useState({ x: 565, y: 125 });
    const [facing, setFacing] = useState("down");
    const [walking, setWalking] = useState(false);
    const heldDirections = useRef([]);
    const speed = 1;

    // Handle keyboard input
    useEffect(() => {
        const handleKeyDown = (e) => {
            let dir = null;
            if (e.key === "ArrowUp" || e.key === "w") dir = directions.up;
            if (e.key === "ArrowDown" || e.key === "s") dir = directions.down;
            if (e.key === "ArrowLeft" || e.key === "a") dir = directions.left;
            if (e.key === "ArrowRight" || e.key === "d") dir = directions.right;
            if (dir && !heldDirections.current.includes(dir)) {
                heldDirections.current.unshift(dir);
            }
        };
        const handleKeyUp = (e) => {
            let dir = null;
            if (e.key === "ArrowUp" || e.key === "w") dir = directions.up;
            if (e.key === "ArrowDown" || e.key === "s") dir = directions.down;
            if (e.key === "ArrowLeft" || e.key === "a") dir = directions.left;
            if (e.key === "ArrowRight" || e.key === "d") dir = directions.right;
            heldDirections.current = heldDirections.current.filter(d => d !== dir);
        };
        window.addEventListener("keydown", handleKeyDown);
        window.addEventListener("keyup", handleKeyUp);
        return () => {
            window.removeEventListener("keydown", handleKeyDown);
            window.removeEventListener("keyup", handleKeyUp);
        };
    }, []);

    // Animation loop
    useEffect(() => {
        let animationFrameId;
        const step = () => {
            let { x, y } = position;
            const held_direction = heldDirections.current[0];
            if (held_direction) {
                if (held_direction === directions.right) x += speed;
                if (held_direction === directions.left) x -= speed;
                if (held_direction === directions.down) y += speed;
                if (held_direction === directions.up) y -= speed;
                setFacing(held_direction);
                setWalking(true);
            } else {
                setWalking(false);
            }
            // WALL
            const leftLimit = 15;
            const rightLimit = 590;
            const topLimit = 100;
            const bottomLimit = 180;
            if (x < leftLimit) x = leftLimit;
            if (x > rightLimit) x = rightLimit;
            if (y < topLimit) y = topLimit;
            if (y > bottomLimit) y = bottomLimit;
            setPosition({ x, y });
            animationFrameId = window.requestAnimationFrame(step);
        };
        animationFrameId = window.requestAnimationFrame(step);
        return () => window.cancelAnimationFrame(animationFrameId);
        // eslint-disable-next-line
    }, [position]);

    // Pixel size
    const pixelSize = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--pixel-size')) || 2;
    const camera_left = pixelSize * 66;
    const camera_top = pixelSize * 42;

    // Character style
    const characterStyle = {
        transform: `translate3d(${position.x * pixelSize}px, ${position.y * pixelSize}px, 0)`
    };
    const mapStyle = {
        transform: `translate3d(${-position.x * pixelSize + camera_left}px, ${-position.y * pixelSize + camera_top}px, 0)`
    };

    // Ambil karakter & nama dari localStorage
    const selectedCharacter = localStorage.getItem("selectedCharacter");
    const playerName = localStorage.getItem("playerName");

    return (
        <>
            <div className="black-screen" id="blackScreen"></div>
            <div className="frame">
                <div className="camera">
                    <div className="map pixel-art" style={mapStyle}> 
                        <div>
                            <div>
                                <div id="quest-display">
                                    <h3>Quests List</h3>
                                    <ul id="quest-list"></ul>
                                </div>
                                <div id="game-day" className="date"></div>
                                <div><img src="./asset/character/coin.png" className="money-picture" /></div>
                                <div className="money-displayed"></div>
                                <progress value="100" max="100" id="bath"></progress>
                                <progress value="100" max="100" id="hunger"></progress>
                                <progress value="100" max="100" id="sleeps"></progress>
                                <progress value="100" max="100" id="happy"></progress>
                                <progress value="100" max="100" id="health"></progress>
                                <div><img src="/asset/iconBar/shower.png" className="bath-picture" /></div>
                                <div><img src="/asset/iconBar/hunger.png" className="hunger-picture" /></div>
                                <div><img src="/asset/iconBar/bed.png" className="sleeps-picture" /></div>
                                <div><img src="/asset/iconBar/happy.png" className="happy-picture" /></div>
                                <div><img src="/asset/iconBar/health.png" className="health-picture" /></div>
                            </div>
                            <div className="button-map "></div>
                            <div className="button-sleep "></div>
                            <div className="button-foto"></div>
                            <div className="button-mandi"></div>
                            <div className="button-makan"></div>
                            <div className="button-tenda"></div>
                            <div className="button-guild"></div>
                            <div className="character-name"></div>
                            <div className="character" facing="down" walking="true">
                                <div className="shadow pixel-art"></div>
                                <div className="character_spritesheet pixel-art"></div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>


            {/* <!--CONTROL--> */}
            <div className="dpad">
                <div className="DemoDirectionUI flex-center">
                    <button className="dpad-button dpad-left">
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 -0.5 13 13" shape-rendering="crispEdges">
                            <path className="Arrow_outline-top" stroke="#5f5f5f"
                                d="M1 0h11M0 1h1M12 1h1M0 2h1M12 2h1M0 3h1M12 3h1M0 4h1M12 4h1M0 5h1M12 5h1M0 6h1M12 6h1M0 7h1M12 7h1M0 8h1M12 8h1" />
                            <path className="Arrow_surface" stroke="#f5f5f5"
                                d="M1 1h11M1 2h11M1 3h5M7 3h5M1 4h4M7 4h5M1 5h3M7 5h5M1 6h4M7 6h5M1 7h5M7 7h5M1 8h11" />
                            <path className="Arrow_arrow-inset" stroke="#434343" d="M6 3h1M5 4h1M4 5h1" />
                            <path className="Arrow_arrow-body" stroke="#5f5f5f" d="M6 4h1M5 5h2M5 6h2M6 7h1" />
                            <path className="Arrow_outline-bottom" stroke="#434343"
                                d="M0 9h1M12 9h1M0 10h1M12 10h1M0 11h1M12 11h1M1 12h11" />
                            <path className="Arrow_edge" stroke="#ffffff" d="M1 9h11" />
                            <path className="Arrow_front" stroke="#cccccc" d="M1 10h11M1 11h11" />
                        </svg>
                    </button>
                    <button className="dpad-button dpad-up">
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 -0.5 13 13" shape-rendering="crispEdges">
                            <path className="Arrow_outline-top" stroke="#5f5f5f"
                                d="M1 0h11M0 1h1M12 1h1M0 2h1M12 2h1M0 3h1M12 3h1M0 4h1M12 4h1M0 5h1M12 5h1M0 6h1M12 6h1M0 7h1M12 7h1M0 8h1M12 8h1" />
                            <path className="Arrow_surface" stroke="#f5f5f5"
                                d="M1 1h11M1 2h11M1 3h11M1 4h5M7 4h5M1 5h4M8 5h4M1 6h3M9 6h3M1 7h11M1 8h11" />
                            <path className="Arrow_arrow-inset" stroke="#434343" d="M6 4h1M5 5h1M7 5h1" />
                            <path className="Arrow_arrow-body" stroke="#5f5f5f" d="M6 5h1M4 6h5" />
                            <path className="Arrow_outline-bottom" stroke="#434343"
                                d="M0 9h1M12 9h1M0 10h1M12 10h1M0 11h1M12 11h1M1 12h11" />
                            <path className="Arrow_edge" stroke="#ffffff" d="M1 9h11" />
                            <path className="Arrow_front" stroke="#cccccc" d="M1 10h11M1 11h11" />
                        </svg>
                    </button>
                    <button className="dpad-button dpad-down">
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 -0.5 13 13" shape-rendering="crispEdges">
                            <path className="Arrow_outline-top" stroke="#5f5f5f"
                                d="M1 0h11M0 1h1M12 1h1M0 2h1M12 2h1M0 3h1M12 3h1M0 4h1M12 4h1M0 5h1M12 5h1M0 6h1M12 6h1M0 7h1M12 7h1M0 8h1M12 8h1" />
                            <path className="Arrow_surface" stroke="#f5f5f5"
                                d="M1 1h11M1 2h11M1 3h11M1 4h3M9 4h3M1 5h4M8 5h4M1 6h5M7 6h5M1 7h11M1 8h11" />
                            <path className="Arrow_arrow-inset" stroke="#434343" d="M4 4h5" />
                            <path className="Arrow_arrow-body" stroke="#5f5f5f" d="M5 5h3M6 6h1" />
                            <path className="Arrow_outline-bottom" stroke="#434343"
                                d="M0 9h1M12 9h1M0 10h1M12 10h1M0 11h1M12 11h1M1 12h11" />
                            <path className="Arrow_edge" stroke="#ffffff" d="M1 9h11" />
                            <path className="Arrow_front" stroke="#cccccc" d="M1 10h11M1 11h11" />
                        </svg>
                    </button>
                    <button className="dpad-button dpad-right">
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 -0.5 13 13" shape-rendering="crispEdges">
                            <path className="Arrow_outline-top" stroke="#5f5f5f"
                                d="M1 0h11M0 1h1M12 1h1M0 2h1M12 2h1M0 3h1M12 3h1M0 4h1M12 4h1M0 5h1M12 5h1M0 6h1M12 6h1M0 7h1M12 7h1M0 8h1M12 8h1" />
                            <path className="Arrow_surface" stroke="#f5f5f5"
                                d="M1 1h11M1 2h11M1 3h5M7 3h5M1 4h5M8 4h4M1 5h5M9 5h3M1 6h5M8 6h4M1 7h5M7 7h5M1 8h11" />
                            <path className="Arrow_arrow-inset" stroke="#434343" d="M6 3h1M7 4h1M8 5h1" />
                            <path className="Arrow_arrow-body" stroke="#5f5f5f" d="M6 4h1M6 5h2M6 6h2M6 7h1" />
                            <path className="Arrow_outline-bottom" stroke="#434343"
                                d="M0 9h1M12 9h1M0 10h1M12 10h1M0 11h1M12 11h1M1 12h11" />
                            <path className="Arrow_edge" stroke="#ffffff" d="M1 9h11" />
                            <path className="Arrow_front" stroke="#cccccc" d="M1 10h11M1 11h11" />
                        </svg>
                    </button>
                </div>
            </div>
        </>
    );
}

export default Indo;
